sys_script_client.script:
function onLoad() {
	//Check to see if the form is for a new record.  If it is a new record, 
	//set the Requested for value to the currently logged in user.

    if(g_form.isNewRecord()){
		alert ("User " + g_user.getFullName() + " creates new record");
        g_form.setValue('u_requested_for',g_user.userID);
    }

	var myVar = 3;
	jslog("This message is from jslog().");   
	jslog("The value of myVar = " + myVar); 
	jslog("The Priority value = " + g_form.getValue('priority'));
	jslog("The first name of the currently logged in user is " + g_user.firstName + ".");
}
